ation_link($data_pagination['total_row']);        $data['contacts'] = $data_pagination['data'];        foreach ($data['contacts'] as $key => $value){			if($cod == 1){				$data['contacts'][$key]['profit'] = round(($value['price_purchase'] - $value['price_purchase'] * $cod_cost / 100 - $value['price_purchase'] * $vat[0] / 100) * $profit[0] / 100) * $teacher_course[$value['course_code']]['percent'];			}else{				$data['contacts'][$key]['profit'] = round(($value['price_purchase'] - $value['price_purchase'] * $vat[0] / 100) * $profit[0] / 100)*$teacher_course[$value['course_code']]['percent'];			}        }				//echo '<pre>'; print_r($data);die;                $data['total_contact'] = $data_pagination['total_row'];        $data['left_col'] = array('course_code', 'l8_c3_switch');        $data['right_col'] = array('date_rgt');        //  $data['right_col'] = array('date_confirm');        $this->table .= 'sale provider date_confirm date_expect_receive_cod note_cod action';        $data['table'] = array('contact_id', 'name', 'course_code', 'price_purchase', 'profit', 'date_rgt'); //array('selection', 'contact_id');        /*         * Các file js cần load         */        $data['load_js'] = array('common_real_filter_contact');        $data['titleListContact'] = 'Danh sách contact đã đăng ký';        $data['actionForm'] = '';        $data['content'] = 'common/list_contact';        $this->load->view(_MAIN_LAYOUT_, $data);    }    private function _get_all_require_data($id = '', $course = '') {        if ($id != '') {            $require_model = array(                'staffs' => array(                    'where' => array(                        'id' => $id,                        'role_id' => 8,                        'active' => 1                    )                ),            );        }        if ($course != '') {            $require_model = array(                'courses' => array(                    'where_in' => array(                        'course_code' => $course					),                    'where' => array(                        'active' => 1                    )                ),            );        }        return array_merge($this->data, $this->_get_require_data($require_model));    }    function view_report() {        $this->load->helper('manager_helper');        $get = $this->input->get();        $query = $this->_get_query_condition_arr($get);        //echo '<pre>';print_r($query);die;        $teacher_id = $this->session->userdata['user_id'];        $teacher_infor = $this->_get_all_require_data($teacher_id, '');        $vat = explode(";", $teacher_infor['staffs'][0]['vat']);        $cod = $teacher_infor['staffs'][0]['cod'];			$cod_cost = $teacher_infor['staffs'][0]['cod_cost'];		        $profit = explode(";", $teacher_infor['staffs'][0]['profit']);        $teacher_course = $this->get_teacher_combo_and_course($teacher_id);        $list_course_code_only = array();        foreach ($teacher_course as $value){            $list_course_code_only[] = $value['course_code'];        }        //        echo '<pre>';//                print_r($list_course_code_only);die;                $data['courses'] = $teacher_course;        /* Mảng chứa các level cần lấy, và ngày phát sinh tương ứng */        $typeReport = array(            'Đăng ký (C3)' => 'date_rgt',            'Đã lọc trùng(L1)' => 'date_handover',            'Đồng ý mua (L6)' => 'date_confirm',            'Hủy đơn' => 'date_receive_cancel_cod',            'Đã thu cod (L7)' => 'date_receive_cod',            'Đã thu Lakita (L8)' => 'date_receive_lakita',        );        /* Mảng chứa các ngày lẻ */        if (isset($get['filter_date_happen_from']) && $get['filter_date_happen_from'] != '' && isset($get['filter_date_happen_end']) && $get['filter_date_happen_end'] != '') {            $startDate = strtotime($get['filter_date_happen_from']);            $endDate = strtotime($get['filter_date_happen_end']);            $dateArray = h_get_time_range($startDate, $endDate);        }        //echo '<pre>';print_r($dateArray);die;        // <editor-fold defaultstate="collapsed" desc="LỌC THEO TỪNG NGÀY LẺ">        if (isset($get['filter_date_happen_from']) && $get['filter_date_happen_from'] != '' && isset($get['filter_date_happen_end']) && $get['filter_date_happen_end'] != '') {            $Report = array();            foreach ($typeReport as $level => $typeDate) {                $total = 0;                foreach ($dateArray as $dayName => $date) {                    $revenue = 0;                    $count = 0;                    $input = array();                    $input['select'] = 'id, price_purchase, source_sale_id, course_code';                    if ($level == 'Đã lọc trùng(L1)') {                        $input['where'] = array(                            '(`ordering_status_id` = 1 OR `ordering_status_id` = ' . _CHAM_SOC_SAU_MOT_THOI_GIAN_ .                            ' OR `ordering_status_id` = ' . _TU_CHOI_MUA_ . ''                            . ' OR `ordering_status_id` = ' . _DONG_Y_MUA_ . ')' => 'NO-VALUE');                    }                    if ($level == 'L1.1') {                        $input['where']['ordering_status_id'] = _SAI_SO_;                    }                    $input['where'][$typeDate . '>='] = $date;                    $input['where'][$typeDate . '<='] = $date + 24 * 3600 - 1;                    $input['where']['is_hide'] = '0';                    if (isset($get['filter_course_code']) && $get['filter_course_code'] != '') {                        $input['where_in'] = array('course_code' => $get['filter_course_code']);                    } else {                        $input['where_in'] = array('course_code' => $list_course_code_only);                    }                    $input = array_merge_recursive($input, $query['input_get']);                    $L8 = $this->contacts_model->load_all($input);                    if ($cod == 1) {                        foreach ($L8 as $value1) {                            switch ($value1['source_sale_id']) {                                case 1:                                    $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $cod_cost / 100 - $value1['price_purchase'] * $vat[0] / 100) * $profit[0] / 100)*$teacher_course[$value1['course_code']]['percent'];                                    break;                                case 2:                                    $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $cod_cost / 100 - $value1['price_purchase'] * $vat[1] / 100) * $profit[1] / 100)*$teacher_course[$value1['course_code']]['percent'];                                    break;                                case 3:                                    $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $cod_cost / 100 - $value1['price_purchase'] * $vat[2] / 100) * $profit[2] / 100)*$teacher_course[$value1['course_code']]['percent'];                                    break;                            }                        }                    } else {                        foreach ($L8 as $value1) {                            switch ($value1['source_sale_id']) {                                case 1:                                    $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $vat[0] / 100) * $profit[0] / 100)*$teacher_course[$value1['course_code']]['percent'];                                    break;                                case 2:                                    $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $vat[1] / 100) * $profit[1] / 100)*$teacher_course[$value1['course_code']]['percent'];                       