tus_id'] = _SAI_SO_;                    }//                    if()                    $input['where'][$typeDate . '>='] = strtotime($week['start_date']);                    $input['where'][$typeDate . '<='] = strtotime($week['end_date']) + 24 * 3600 - 1;                    $input['where']['is_hide'] = '0';                    if (isset($get['filter_course_code']) && $get['filter_course_code'] != '') {                        $input['where_in'] = array('course_code' => $get['filter_course_code']);                    } else {                        $input['where_in'] = array('course_code' => $list_course_code_only);                    }                    $input = array_merge_recursive($input, $query['input_get']); // Nếu có lọc khách thì thêm vào điều kiện query                    // $weekName = 'Tuần ' . $week['week_id'];                    $weekName = $weekFullName[$week['week_id']];                    $L8 = $this->contacts_model->load_all($input);                    if ($cod == 1) {                        foreach ($L8 as $value1) {                            switch ($value1['source_sale_id']) {                                case 1:                                    $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $cod_cost / 100 - $value1['price_purchase'] * $vat[0] / 100) * $profit[0] / 100)*$teacher_course[$value1['course_code']]['percent'];                                    break;                                case 2:                                    $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $cod_cost / 100 - $value1['price_purchase'] * $vat[1] / 100) * $profit[1] / 100)*$teacher_course[$value1['course_code']]['percent'];                                    break;                                case 3:                                    $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $cod_cost / 100 - $value1['price_purchase'] * $vat[2] / 100) * $profit[2] / 100)*$teacher_course[$value1['course_code']]['percent'];                                    break;                            }                        }                    } else {                        foreach ($L8 as $value1) {                            switch ($value1['source_sale_id']) {                                case 1:                                    $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $vat[0] / 100) * $profit[0] / 100)*$teacher_course[$value1['course_code']]['percent'];                                    break;                                case 2:                                    $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $vat[1] / 100) * $profit[1] / 100)*$teacher_course[$value1['course_code']]['percent'];                                    break;                                case 3:                                    $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $vat[2] / 100) * $profit[2] / 100)*$teacher_course[$value1['course_code']]['percent'];                                    break;                            }                        }                    }                    $count = count($L8);                    $result = array($count, $revenue);                    $Report[$level][$weekName]['Thực đạt'] = $result;                    $total += $Report[$level][$weekName]['Thực đạt'][0];                    $total1 += $Report[$level][$weekName]['Thực đạt'][1];                }                $Report[$level]['Tháng']['Thực đạt'] = array($total, $total1);//                if (!isset($get['filter_month_id'])) {//                    foreach ($dateArray as $dayName => $date) {//                        $revenue = 0;//                        $count = 0;//                        $input = array();//                        $input['select'] = 'id';//                        if ($level == 'Đã lọc trùng(L1)') {//                            $input['where'] = array(//                                '(`ordering_status_id` = 1 OR `ordering_status_id` = ' . _CHAM_SOC_SAU_MOT_THOI_GIAN_ .//                                ' OR `ordering_status_id` = ' . _TU_CHOI_MUA_ . ''//                                . ' OR `ordering_status_id` = ' . _DONG_Y_MUA_ . ')' => 'NO-VALUE');//                        }//                        if ($level == 'L1.1') {//                            $input['where']['ordering_status_id'] = _SAI_SO_;//                        }//                        $input['where'][$typeDate . '>='] = $date;//                        $input['where'][$typeDate . '<='] = $date + 24 * 3600 - 1;//                        $input['where']['is_hide'] = '0';//                        if (isset($get['filter_course_code']) && $get['filter_course_code'] != '') {//                            $input['where_in'] = array('course_code' => $get['filter_course_code']);//                        } else {//                            $input['where_in'] = array('course_code' => $teacher_course);//                        }//                        $input = array_merge_recursive($input, $query['input_get']);//                        $L8 = $this->contacts_model->load_all($input);//                        if ($cod == 1) {//                            foreach ($L8 as $value1) {//                                $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $vat / 100 - $value1['cod_fee']) * $profit / 100);//                            }//                        } else {//                            foreach ($L8 as $value1) {//                                $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $vat / 100) * $profit / 100);//                            }//                        }//                        $count = count($L8)