d'];		$cod_cost = $teacher_infor['staffs'][0]['cod_cost'];        $profit = explode(";", $teacher_infor['staffs'][0]['profit']);        $teacher_course = $this->get_teacher_combo_and_course($teacher_id);        if (!count($get)) {            $startDate = strtotime(date('01-m-Y'));            $endDate = mktime(0, 0, 0, date('m') + 1, 0, date('Y'));        } else {            if (isset($get['filter_date_happen_from']) && $get['filter_date_happen_from'] != '' && isset($get['filter_date_happen_end']) && $get['filter_date_happen_end'] != '') {                $startDate = strtotime($get['filter_date_happen_from']);                $endDate = strtotime($get['filter_date_happen_end']);            } else if (isset($get['filter_month_id']) && $get['filter_month_id'] != '') {                $startDate = strtotime('1-' . date('m-Y', strtotime($get['filter_month_id'])));                $endDate = mktime(0, 0, 0, date('m', strtotime($get['filter_month_id'])) + 1, 0, date('Y', strtotime($get['filter_month_id'])));            } else {                $startDate = strtotime(date('01-m-Y'));                $endDate = mktime(0, 0, 0, date('m') + 1, 0, date('Y'));            }        }        // echo $startDate .'--'. $endDate;die();		$total_revenue = 0;        foreach ($teacher_course as $key => $value) {            $revenue = 0;            $input = array();            $input['select'] = 'price_purchase,source_sale_id';            $input['where'] = array(							'course_code' => $value['course_code'],                // 'cod_status_id' => _DA_THU_COD_,                'date_receive_cod >=' => $startDate,                'date_receive_cod <' => $endDate,                'is_hide' => '0'			);            $L7 = $this->contacts_model->load_all($input);            $input = array();            $input['select'] = 'price_purchase,source_sale_id,course_code';            $input['where'] = array(							'course_code' => $value['course_code'],                // 'cod_status_id' => _DA_THU_LAKITA_,                'date_receive_lakita >=' => $startDate,                'date_receive_lakita <' => $endDate,                'is_hide' => '0'			);            $L8 = $this->contacts_model->load_all($input);            $L7L8 = array_merge($L7, $L8);			//echo '<pre>';print_r($L7L8);die;            if ($cod == 1) {                foreach ($L7L8 as $value1) {                    switch ($value1['source_sale_id']) {                        case 1:                            $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $cod_cost / 100 - $value1['price_purchase'] * $vat[0] / 100) * $profit[0] / 100)*$teacher_course[$value1['course_code']]['percent'];                            break;                        case 2:                            $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $cod_cost / 100 - $value1['price_purchase'] * $vat[1] / 100) * $profit[1] / 100)*$teacher_course[$value1['course_code']]['percent'];                            break;                        case 3:                            $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $cod_cost / 100 - $value1['price_purchase'] * $vat[2] / 100) * $profit[2] / 100)*$teacher_course[$value1['course_code']]['percent'];                            break;                    }                }            } else {                foreach ($L7L8 as $value1) {                    switch ($value1['source_sale_id']) {                        case 1:                            $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $vat[0] / 100) * $profit[0] / 100)*$teacher_course[$value1['course_code']]['percent'];                            break;                        case 2:                            $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $vat[1] / 100) * $profit[1] / 100)*$teacher_course[$value1['course_code']]['percent'];                            break;                        case 3:                            $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $vat[2] / 100) * $profit[2] / 100)*$teacher_course[$value1['course_code']]['percent'];                            break;                    }                }            }            $data['course_revenue'][$value['course_code']] = $revenue;			$total_revenue +=$revenue;			        }		$data['course_revenue']['Tổng'] += $total_revenue;        $data['left_col'] = array('month_id_teacher', 'date_happen');        //  $data['right_col'] = array('date_confirm');        $data['startDate'] = $startDate;        $data['endDate'] = $endDate;        $data['title_content'] = 'Danh sách đăng ký';        $data['content'] = 'teacher/top_revenue';        $this->load->view(_MAIN_LAYOUT_, $data);    }	    function array_sort($array, $on, $order = SORT_ASC) {        $new_array = array();        $sortable_array = array();        if (count($array) > 0) {            foreach ($array as $k => $v) {                if (is_array($v)) {                    foreach ($v as $k2 => $v2) {                        if ($k2 == $on) {                            $sortable_array[$k] = $v2;                        }                    }                } else {                    $sortable_array[$k] = $v;                }            }            switch ($order) {                case SORT_ASC:                    asort($sortable_array);                    break;                case SORT_DESC:                    arsort($sortable_array);                    break;            }            foreach ($sortable_array as $k => $v) {                $new_array[$k] = $array[$k];            }        }        return $new_array;    }    private function get_data_top_revenue() {        $require_model = array(            'staffs' => array(                'where' => array(                    'role_id' => 7,                    'active' => 1                )            )        );        return array_merge($this->data, $this->_get_require_data($require_model));    }    public function ViewReport($offset = 0) {        $get = $this->input->get();//        $this->load->model('call_status_model');//        $this->load->model('ordering_status_model');//        $this->load->model('cod_status_model');//        $this->load->model('providers_model');//        $this->load->model('payment_method_rgt_model');        $conditional['select'] = 'course_code,  price_purchase, date_receive_lakita';        if ((isset($get['filter_date_date_rgt']) && $get['filter_date_date_rgt'] == '')) {            $conditional['where'] = array('date_rgt >=' => strtotime(date('Y-m-01 00:00:00')), 'cod_status_id' => 3, 'is_hide' => '0');        } else {            $conditional['where'] = array('cod_status_id' => 3, 'is_hide' => '0');        }        $data_pagination = $this->_query_all_from_get($get, $conditional, 10000, $offset);        $contacts = $data_pagination['data'];        $rs = [];        foreach ($contacts as $key => $value) {            $rs[$key]['STT'] = $key + 1;            $rs[$key]['Mã khóa học'] = $value['course_code'];            //$rs[$key]['Ngày nhận tiền'] = date('Y-m-d', $value['date_receive_lakita']);            $rs[$key]['Giá mua khóa học'] = ($value['price_purchase']);        }        echo json_encode($rs);        die;    }	    function view_all_teacher_report() {        $this->load->helper('manager_helper');        $this->load->model('staffs_model');        $get = $this->input->get();        /* Mảng chứa các level cần lấy, và ngày phát sinh tương ứng */        $typeReport = array(            'L7' => 'date_receive_cod',            'L8' => 'date_receive_lakita',        );        /* Mảng chứa các ngày lẻ */        if (isset($get['filter_date_happen_from']) && $get['filter_date_happen_from'] != '' && isset($get['filter_date_happen_end']) && $get['filter_date_happen_end'] != '') {            $startDate = strtotime($get['filter_date_happen_from']);            $endDate = strtotime($get['filter_date_happen_end']);        } else {            $startDate = strtotime(date('1-m-Y'));            $endDate = strtotime(date('d-m-Y'));        }        $input = array();        $input['select'] = 'id';        $input['where']['role_id'] = 8;        $input['where']['active'] = 1;        $list_teacher = $this->staffs_model->load_all($input);        $total = 0;        foreach ($list_teacher as $t_value) {            $teacher_id = $t_value['id'];            $teacher_infor = $this->_get_all_require_data($teacher_id, '');            $vat = explode(";", $teacher_infor['staffs'][0]['vat']);            $cod = $teacher_infor['staffs'][0]['cod'];            $profit = explode(";", $teacher_infor['staffs'][0]['profit']);            $teacher_course = explode(';', $teacher_infor['staffs'][0]['course']);            $data = $this->_get_all_require_data('', $teacher_course);            $Report = array();            foreach ($typeReport as $level => $typeDate) {                $revenue = 0;                $input = array();                $input['select'] = 'id, price_purchase, source_sale_id';                $input['where'][$typeDate . '>='] = $startDate;                $input['where'][$typeDate . '<='] = $endDate + 24 * 3600 - 1;                $input['where']['is_hide'] = '0';                if ($level == 'L7') {                    $input['where']['cod_status_id'] = 2;                }                if ($level == 'L8') {                    $input['where']['cod_status_id'] = 3;                }                $input['where_in'] = array('course_code' => $teacher_course);                $L8 = $this->contacts_model->load_all($input);                if ($cod == 1) {                    foreach ($L8 as $value1) {                        switch ($value1['source_sale_id']) {                            case 1:                                $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * 12 / 100 - $value1['price_purchase'] * $vat[0] / 100) * $profit[0] / 100);                                break;                            case 2:                                $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * 12 / 100 - $value1['price_purchase'] * $vat[1] / 100) * $profit[1] / 100);                                break;                            case 3:                                $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * 12 / 100 - $value1['price_purchase'] * $vat[2] / 100) * $profit[2] / 100);                                break;                        }                    }                } else {                    foreach ($L8 as $value1) {                        switch ($value1['source_sale_id']) {                            case 1:                                $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $vat[0] / 100) * $profit[0] / 100);                                break;                            case 2:                                $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $vat[1] / 100) * $profit[1] / 100);                                break;                            case 3:                                $revenue += round(($value1['price_purchase'] - $value1['price_purchase'] * $vat[2] / 100) * $profit[2] / 100);                                break;                        }                    }                }                $Report[$level] = $revenue;            }            $total += $Report['L7'] + $Report['L8'];            $Report3[$teacher_infor['staffs'][0]['name']] = $Report['L7'] + $Report['L8'];        }        $Report3['Tổng'] = $total;        $data = $this->_get_all_require_data('', $teacher_course);        $data['Report'] = $Report3;        $data['startDate'] = date('d-m-Y',isset($startDate) ? $startDate : '0');        $data['endDate'] = date('d-m-Y',isset($endDate) ? $endDate : '0');        $data['left_col'] = array('date_happen');        $data['right_col'] = array();        $data['content'] = 'teacher/view_report_all_teacher';        $this->load->view(_MAIN_LAYOUT_, $data);    }        function get_teacher_combo_and_course($teacher_id = 80 ){        $this->load->model('staffs_model');        $this->load->model('courses_model');        $input = array();        $input['select'] = 'course';        $input['where']['id'] = $teacher_id;        $course = $this->staffs_model->load_all($input);        $course = $course[0]['course'];        $course = explode(';', $course);        $list_course = array();        foreach ($course as $value){            $list_course[$value] =  1 ;            $input = array();            $input['select'] = 'course_code,combo_course';            $input['where']['is_combo'] = '1';            $input['like']['combo_course'] = $value;            $combo_course = $this->courses_model->load_all($input);            if(!empty($combo_course)){                foreach ($combo_course as $value1){                $list = explode(',', $value1['combo_course']);                $count = $count_course_in_combo = count($list);                foreach ($list as $value){                    $input = array();                    $input['where']['id'] = $teacher_id;                    $input['like']['course'] = $value;                    if(empty($this->staffs_model->load_all($input))){                        $count_course_in_combo = $count_course_in_combo - 1;                    }                }                $list_course[$value1['course_code']] = $count_course_in_combo/$count ;                }            }        }        $result = array();        foreach ($list_course as $key => $value){            $result[$key] = array('course_code'=> $key,'percent' => $value);        }        return ($result);    }        }